// Подключение списка активных модулей

document.addEventListener('DOMContentLoaded', function () {
	function updateTimer() {
		const now = new Date()
		const endOfDay = new Date(
			now.getFullYear(),
			now.getMonth(),
			now.getDate() + 1
		)
		const left = endOfDay - now

		const leftHours = Math.floor(left / (1000 * 60 * 60))
		const leftMinutes = Math.floor((left / (1000 * 60)) % 60)
		const leftSeconds = Math.floor((left / 1000) % 60)

		function declension(num, words) {
			return words[
				num % 100 > 4 && num % 100 < 20
					? 2
					: [2, 0, 1, 1, 1, 2][num % 10 < 5 ? num % 10 : 5]
			]
		}

		const hoursText = declension(leftHours, ['час', 'часа', 'часов'])
		const minutesText = declension(leftMinutes, ['минута', 'минуты', 'минут'])
		const secondsText = declension(leftSeconds, [
			'секунда',
			'секунды',
			'секунд',
		])

		const hours = (document.querySelector('.timer__hour').textContent =
			leftHours)
		const hoursName = (document.querySelector(
			'.timer__hour ~ span'
		).textContent = hoursText)

		const min = (document.querySelector('.timer__min').textContent =
			leftMinutes)
		const minName = (document.querySelector('.timer__min ~ span').textContent =
			minutesText)

		const sec = (document.querySelector('.timer__sec').textContent =
			leftSeconds)
		const secName = (document.querySelector('.timer__sec ~ span').textContent =
			secondsText)
	}

	setInterval(updateTimer, 1000)
	updateTimer() //
})

window.addEventListener('mouseout', function (event) {
	if (
		event.clientY <= 0 ||
		event.clientX <= 0 ||
		event.clientX >= window.innerWidth ||
		event.clientY >= window.innerHeight
	) {
		showPopup()
	}
})

function showPopup() {
	const popup = document.querySelector('#popup')
	popup.classList.add('popup_show')
	document.documentElement.classList.add('popup-show')
}

const popup = document.querySelector('#popup')
popup.addEventListener('click', function (e) {
	if (e.target.className === 'popup__wrapper') {
		const popupUp = document.querySelector('#popup')
		popupUp.classList.remove('popup_show')
		document.documentElement.classList.remove('popup-show')
	}
})

