let timeout
// Функция для закрытия всплывающего окна
const popupTimeButton = document.querySelector('.popupTime button')
popupTimeButton.addEventListener('click', function (e) {
	closePopup1()
})

function closePopup1() {
	const popup = document.querySelector('.popupTime')
	popup.style.display = 'none'
	clearTimeout(timeout)
	setTimeout(() => {
		showRandomPopup()
	}, 10000)
}

// Функция для отображения случайного всплывающего окна
function showRandomPopup() {
	const popupIndex = Math.floor(Math.random() * popups.length)
	const popup = document.querySelector('.popupTime')
	const popupTitle = popup.querySelector('.popupTime__desc')
	const popupContent = popup.querySelector('.popupTime__num')
	const popupSvg = popup.querySelector('svg')

	popupTitle.textContent = popups[popupIndex].title
	popupContent.textContent = popups[popupIndex].content
	popupSvg.innerHTML = popups[popupIndex].img

	popup.style.display = 'flex'
	timeout = setTimeout(closePopup1, 10000)
}

// Проверка на мобильную версию
if (window.innerWidth > 600) {
	showRandomPopup()
}

